import { ChevronLeft, ChevronRight } from "lucide-react";

function ClearCarPromo() {
  return (
    <div className="relative w-full">
      <NavButton direction="left" />

      <div className="text-center px-12">
        <h2 className="text-3xl font-bold text-gray-800 mb-6">ClearCar</h2>
        <div className="mb-8">
          <img
            src="https://images.unsplash.com/photo-1619767886558-efdc259b6e09?auto=format&fit=crop&q=80&w=800"
            alt="Modern Car"
            className="rounded-lg shadow-xl mx-auto"
          />
        </div>
        <p className="text-lg text-gray-600 mb-4">
          Changing the game with condition-enhanced pricing
        </p>
        <p className="text-sm text-gray-500 mb-6">
          ClearCar: Our new offering is revolutionizing the acquisition process
          with real-time, data-driven pricing for your wholesale inventory at
          ACV.
        </p>
        <button className="bg-blue-600 text-white px-6 py-2 rounded-md hover:bg-blue-700 transition-colors">
          GET MORE DETAILS
        </button>
      </div>

      <NavButton direction="right" />
    </div>
  );
}

function NavButton({ direction }) {
  const Icon = direction === "left" ? ChevronLeft : ChevronRight;
  const position = direction === "left" ? "left-0" : "right-0";

  return (
    <button
      className={`absolute ${position} top-1/2 -translate-y-1/2 bg-white rounded-full p-2 shadow-lg`}
    >
      <Icon className="h-6 w-6 text-gray-600" />
    </button>
  );
}

export default ClearCarPromo;
